
import React, { useEffect, useState } from 'react'



function Slider() {
        const [currentIndex,setCurrentIndex]=useState(0);
      const [cardsToShow,setcardsToShow]=useState(1);

       useEffect(()=>{
            const updateCardToShow=()=>{
                  if(window.innerWidth>=1023){
                        setcardsToShow(6);

                  }else{
                        setcardsToShow(1)
                  }
            };
            updateCardToShow();
              window.addEventListener('resize',updateCardToShow);
            return ()=> window.removeEventListener('resize',updateCardToShow);

          
            

      },[])



       const nextproject=()=>{
            setCurrentIndex((prevIndex)=>(prevIndex +1) % 6 )

      }
       const prevproject=()=>{
            setCurrentIndex((prevIndex)=>prevIndex === 0 ? 6:prevIndex-1 ) 


      }


  return (
      <div className="mt-100">
            <div>
      <button onClick={prevproject} className="font-bold text-lg ">
            <i  class="fa-solid fa-angle-left"></i>
      </button>
       <button onClick={nextproject}   className="font-bold text-lg">
          <i  class="fa-solid fa-angle-right"></i>
      </button>
      </div>
        <div className="overflow-hidden">
      <div className=" flex gap-8 transition-transform duration-500 ease-in-out mx-60  "
      style={{transform:`translateX(-${(currentIndex*100)/ cardsToShow}%)`}} >
            <img src="data:image/jpeg;base64,"/>
            <img src="data:image/jpeg;base64,"/>
            <img src="data:image/jpeg;base64,"/>
             <img src="data:image/jpeg;base64,"/>
            <img src="data:image/jpeg;base64,"/>

            <img src="data:image/jpeg;base64,"/>

      </div>
      </div>

      


      


     


      
    </div>
  )
}

export default Slider
